import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { IMarket } from "../interface/IMarket.sol";
import { IStrips } from "../interface/IStrips.sol";
import { IStripsLpToken } from "../interface/IStripsLpToken.sol";
import { ISUSD } from "../interface/ISUSD.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

//SLPToken - per market
contract SUSD is 
    ERC20, 
    Ownable,
    ReentrancyGuard,
    ISUSD
{

    mapping (address => bool) public users;
    uint8 private DECIMALS;
    uint public MAXPERUSER;

    constructor() 
        ERC20("Strips USD", "SUSD") 
    {
        DECIMALS = 18;
        MAXPERUSER = 1000 * (10 ** DECIMALS);
    }

    function decimals() public view virtual override returns (uint8) {
        return DECIMALS;
    }


    function receiveSUSD() external override nonReentrant{
        require(users[msg.sender] == false, "MAX_RECEIVED");

        _mint(msg.sender, MAXPERUSER);

        users[msg.sender] = true;
    }


    function mint(address to, uint256 amount) external override onlyOwner {
        require(amount > 0, "Wrong amount");
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external override onlyOwner {
        require(amount > 0, "Wrong amount");
        _burn(from, amount);
    }
}
